t><!-- OPTIONAL SCRIPTS --><script src="plugins/chart.js/Chart.min.js"></script><script src="dist/js/demo.js"></script><script src="dist/js/pages/dashboard3.js"></script><script src="//cdn.datatables.net/1.10.20/js/jquery.dataTables.min.js"></script><script>    $(document).ready( function () {        $('#datatable').DataTable();    } );</script><script>    var ticksStyle = {        fontColor: '#495057',        fontStyle: 'bold'    }    var mode      = 'index'    var intersect = true    var $visitorsChart = $('#visitors-chart')    var visitorsChart  = new Chart($visitorsChart, {        data   : {            labels  : ['Mart', 'Nisan', 'Mayıs', 'Haziran', 'Temmuz', 'Ağustos', 'Eylül'],            datasets: [{                type                : 'line',                data                : [@foreach($data as $list => $key) {{ $key->total }}, @endforeach],                backgroundColor     : 'transparent',                borderColor         : '#007bff',                pointBorderColor    : '#007bff',                pointBackgroundColor: '#007bff',                fill                : false                // pointHoverBackgroundColor: '#007bff',                // pointHoverBorderColor    : '#007bff'            },                {                    type                : 'line',                    data                : [60, 80, 70, 67, 80, 77, 100],                    backgroundColor     : 'tansparent',                    borderColor         : '#ced4da',                    pointBorderColor    : '#ced4da',                    pointBackgroundColor: '#ced4da',                    fill                : false                    // pointHoverBackgroundColor: '#ced4da',                    // pointHoverBorderColor    : '#ced4da'                }]        },        options: {            maintainAspectRatio: false,            tooltips           : {                mode     : mode,                intersect: intersect            },            hover              : {                mode     : mode,                intersect: intersect            },            legend             : {                display: false            },            scales             : {                yAxes: [{                    // display: false,                    gridLines: {                        display      : true,                        lineWidth    : '4px',                        color        : 'rgba(0, 0, 0, .2)',                        zeroLineColor: 'transparent'                    },                    ticks    : $.extend({                        beginAtZero : true,                        suggestedMax: 200                    }, ticksStyle)                }],                xAxes: [{                    display  : true,                    gridLines: {                        display: false                    },                    ticks    : ticksStyle                }]            }        }    })</script></body></html>